import React from 'react';
import connect from 'react-redux';

import classes from './MovieThumbnail.module.css';

const MovieThumbnail = (props) => {

    let attachedClasses = [classes.MovieThumbnail];
    if (props.row === this.props.selectedRow &&
        props.column === this.props.selectedColumn) {
        attachedClasses.push(classes.selected)
    }

    return (
        <div className={classes.MovieThumbnail}>
            <img src={`https://image.tmdb.org/t/p/w500${props.thumbnail}`} alt={props.title}/>
        </div>
    );
};

const dataFromStore = state => {
    return {
        selectedRow: state.selectedRow,
        selectedColumn: state.selectedColumn
    }
}

export default connect(dataFromStore)(MovieThumbnail);
