import React, { Component } from 'react';
import { connect } from 'react-redux';
import axios from 'axios';

import * as actionTypes from '../../store/actions';

import classes from './GenreSlider.module.css';

class GenreSlider extends Component {

    constructor (props) {
        super(props);
        this.loadMoviesHandler();   
    }

    /* 
    for (let genre of this.props.genres) {
            await axios.get(`https://api.themoviedb.org/3/discover/movie?with_genres=${genre.id}&api_key=${this.props.apiKey}`)
                .then(response => {
                    this.props.storeMovies(genre.name, response.data.results.slice(0, 6));
                });
        }
    */

    async loadMoviesHandler() {
        for (let genre of this.props.genres) {
            if (genre.name === this.props.genre) {
                await axios.get(`https://api.themoviedb.org/3/discover/movie?with_genres=${genre.id}&api_key=${this.props.apiKey}`)
                    .then(response => {
                        let moviesByGenre = {
                        genre: genre,
                        movies: response.data.results.slice(0, 1)
                    }
                    this.props.storeMovies(moviesByGenre);               
                });
            }
        }
    }

    render () {
        return (
            <div className={classes.GenreSlider}>
                <p>{this.props.genre}</p>
            </div>
        );
    }
};

const dataFromStore = state => {
    return {
        apiKey: state.apiKey,
        genres: state.genres,
        movies: state.movies
    }
}

const dispatching = dispatch => {
    return {
        storeMovies: (moviesByGenre) => dispatch({
            type: actionTypes.STORE_MOVIES, 
            moviesByGenre: moviesByGenre
        })
    }
}

export default connect(dataFromStore, dispatching)(GenreSlider);
