import React, { Component } from 'react';
import { connect } from 'react-redux';
import axios from 'axios';

import * as actionTypes from '../../store/actions';

import classes from './GenreSlider.module.css';

class GenreSlider extends Component {

    constructor (props) {
        super(props);
        this.loadMoviesHandler();
    }

    async loadMoviesHandler() {
        for (let genre of this.props.genres) {
            await axios.get(`https://api.themoviedb.org/3/discover/movie?with_genres=${genre.id}&api_key=d38aa8716411ef7d8e9054b34a6678ac`)
                .then(response => {
                    this.props.storeMovies(genre.name, response.data.results.slice(0, 6));
                });
        }
    }

    render () {
        console.log(this.props.movies);
        return (
            <div className={classes.GenreSlider}>
                <p>{this.props.genre}</p>
            </div>
        );
    }
};

const dataFromStore = state => {
    return {
        genres: state.genres,
        movies: state.movies
    }
}

const dispatching = dispatch => {
    return {
        storeMovies: (genre, moviesArray) => dispatch({type: actionTypes.STORE_MOVIES, moviesByGenre: {genre: genre, movies: moviesArray}})
    }
}

export default connect(dataFromStore, dispatching)(GenreSlider);
