import React, { Component } from 'react';
import { connect } from 'react-redux';
import axios from 'axios';

import * as actionTypes from '../../store/actions';

import GenreSlider from '../../components/GenreSlider/GenreSlider';

import classes from './Home.module.css';

class Home extends Component {

    componentDidMount() {
        this.loadGenresHandler();
        document.addEventListener("keyup", this.selectedMovieHandler);
    }

    selectedMovieHandler = (event) => {
        let row = this.props.selectedRow;
        let column = this.props.selectedColumn;
        switch (event.keyCode) {
            case 37:
                if (column > 0) {
                    column--;
                } else {
                    column = this.props.numberOfMoviesPerGenre;
                }
                break;
            case 38:
                if (row < this.props.numberOfGenres) {
                    row++;
                } else {
                    row = 0;
                }
                break;
            case 39:
                if (column < this.props.numberOfMoviesPerGenre) {
                    column++;
                } else {
                    column = 0;
                }
                break;
            case 40:
                if (row > 0) {
                    row--;
                } else {
                    row = this.props.numberOfGenres;
                }
                break;
            default:
                console.log("Nope!");
                break;                
        }
        this.props.setSelectedRow(row);
        this.props.setSelectedColumn(column);
        console.log("Row: " + row + ", column: " + column);
    }

    loadGenresHandler() {
        axios.get(`https://api.themoviedb.org/3/genre/movie/list?api_key=${this.props.apiKey}&language=en-US`)
            .then(response => this.props.storeGenres(response.data.genres.slice(0, this.props.numberOfGenres)));
    }

    render () {
        return (
            <div className={classes.Home}>
                {this.props.genres.map(genre => {
                    return <GenreSlider key={genre.id} id={genre.id} genre={genre.name}/>;
                })}
            </div>
        );

    }
}

const dataFromStore = state => {
    return {
        apiKey: state.apiKey,
        numberOfGenres: state.numberOfGenres,
        numberOfMoviesPerGenre: state.numberOfMoviesPerGenre,
        genres: state.genres,
        selectedRow: state.selectedRow,
        selectedColumn: state.selectedColumn
    }
}

const dispatching = dispatch => {
    return {
        storeGenres: (genresArray) => dispatch({
            type: actionTypes.STORE_GENRES,
            genres: genresArray
        }),
        setSelectedRow: (row) => dispatch({
            type: actionTypes.STORE_ROW,
            row: row
        }),
        setSelectedColumn: (column) => dispatch({
            type: actionTypes.STORE_COLUMN,
            column: column
        })
    }
}

export default connect(dataFromStore, dispatching)(Home);
