import React, { Component } from 'react';
import { connect } from 'react-redux';
import axios from 'axios';

import * as actionTypes from '../../store/actions';

import classes from './GenreSlider.module.css';

class GenreSlider extends Component {

    constructor (props) {
        super(props);
    }

    async loadMoviesHandler() {
            await axios.get(`https://api.themoviedb.org/3/discover/movie?with_genres=${this.props.id}&api_key=d38aa8716411ef7d8e9054b34a6678ac`)
                .then(response => console.log(response));

    }

    render () {
        this.loadMoviesHandler();
        return (
            <div className={classes.GenreSlider}>
                <p>{this.props.genre}</p>
            </div>
        );
    }
};

const dataFromStore = state => {
    return {
        genres: state.genres
    }
}

const dispatching = dispatch => {
    return {
        storeMovies: (moviesArray) => dispatch({type: actionTypes.STORE_MOVIES, movies: moviesArray})
    }
}

export default connect(dataFromStore, dispatching)(GenreSlider);
