import * as actionTypes from './actions';

const initialState = {
    apiKey: 'd38aa8716411ef7d8e9054b34a6678ac',
    genres: []
}

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case actionTypes.STORE_GENRES:
            return {
                ...state,
                genres: action.genres
            }
    }
}

export default reducer;
