import React from 'react';
import { connect } from 'react-redux';
import axios from 'axios';

import classes from './GenreSlider.module.css';

const GenreSlider = (props) => {

    let m;

    async function loadMoviesHandler() {
        const res = await axios.get(`https://api.themoviedb.org/3/discover/movie?with_genres=${props.id}&api_key=d38aa8716411ef7d8e9054b34a6678ac`);
        m = response.data.results.slice(0, 6);
    }

    return (
        <div className={classes.GenreSlider}>
            <p>{props.genre}</p>
        </div>
    );
};

const dataFromStore = state => {
    return {
        genres: state.genres
    }
}

export default connect(dataFromStore)(GenreSlider);
