import React, { Component } from 'react';
import { connect } from 'react-redux';
import axios from 'axios';
import * as actionTypes from '../../store/actions';

class Home extends Component {

    constructor(props) {
        this.loadGenresHandler();
    }

    loadGenresHandler = () => {
        axios.get(`https://api.themoviedb.org/3/genre/movie/list?api_key=${this.props.apiKey}&language=en-US`)
            .then(response => {
                this.props.storeGenresHandler(response.data.genres)
            })
            .catch(error => console.log(error));
    }

    render () {
        return <h1>Home page</h1>;
    }
}

const dataFromStore = state => {
    return {
        apiKey: state.apiKey,
        genres: state.genres
    }
}

const dispatching = dispatch => {
    return {
        storeGenresHandler: (genresArray) => dispatch({type: actionTypes.STORE_GENRES, genres: genresArray})
    }
}

export default connect(dataFromStore, dispatching)(Home);
