import React, { Component } from 'react';
import { connect } from 'react-redux';
import axios from 'axios';

import * as actionTypes from '../../store/actions';

import MovieThumbnail from './MovieThumbnail/MovieThumbnail';

import classes from './GenreSlider.module.css';

class GenreSlider extends Component {

    componentDidMount() {
        this.loadMoviesHandler();  
    }

    async loadMoviesHandler() {
        await axios.get(`https://api.themoviedb.org/3/discover/movie?with_genres=${this.props.id}&api_key=${this.props.apiKey}`)
            .then(response => {
                let moviesByGenre = {
                genre: this.props.genre,
                movies: response.data.results.slice(0, this.props.numberOfMoviesPerGenre)
            }
            console.log(moviesByGenre);
            this.props.storeMovies(moviesByGenre);  
        });
    }

    selectedMovieHandler = (arrowButton) => {
        let row = 0;
        let column = 0;
        switch (arrowButton) {
            case 'left':
                if (column < this.props.numberOfMoviesPerGenre) {
                    column++;
                } else {
                    column = 0;
                }
                break;
            case 'up':
                if (row < this.props.numberOfGenres) {
                    row++;
                } else {
                    row = 0;
                }
                break;
            case 'right':

                break;
            case 'bottom':
                break;
            default:
                console.log("Nope!");
                break;                
        }
    }

    render () {
        return (
            <div className={classes.GenreSlider}>
                <p>{this.props.genre}</p>
                <div className={classes.movies}>
                    {this.props.movies.map(genre => {
                        if (genre.genre === this.props.genre) {
                            return [...genre.movies].map(movie => {
                                return <MovieThumbnail key={movie.id} title={movie.title} thumbnail={movie.backdrop_path}/>
                            });
                        }
                    })}
                </div>
            </div>
        );
    }
};

const dataFromStore = state => {
    return {
        apiKey: state.apiKey,
        genres: state.genres,
        numberOfGenres: state.numberOfGenres,
        numberOfMoviesPerGenre: state.numberOfMoviesPerGenre,
        movies: state.movies
    }
}

const dispatching = dispatch => {
    return {
        storeMovies: (moviesByGenre) => dispatch({
            type: actionTypes.STORE_MOVIES, 
            moviesByGenre: moviesByGenre
        })
    }
}

export default connect(dataFromStore, dispatching)(GenreSlider);
