import React, { Component } from 'react';
import { connect } from 'react-redux';
import axios from 'axios';

import * as actionTypes from '../../store/actions';

import GenreSlider from '../../components/GenreSlider/GenreSlider';

import classes from './Home.module.css';

class Home extends Component {

    constructor(props) {
        super(props);

    }

    componentDidMount() {
        this.loadGenresHandler();
    }

    async loadGenresHandler() {
        await axios.get(`https://api.themoviedb.org/3/genre/movie/list?api_key=${this.props.apiKey}&language=en-US`)
            .then(response => this.props.storeGenres(response.data.genres.slice(0, 2)));
    }

    render () {
        return (
            <div className={classes.Home}>
                {this.props.genres.map(genre => {
                    return <GenreSlider key={genre.id} genre={genre.name}/>;
                })}
            </div>
        );

    }
}

const dataFromStore = state => {
    return {
        apiKey: state.apiKey,
        genres: state.genres
    }
}

const dispatching = dispatch => {
    return {
        storeGenres: (genresArray) => dispatch({
            type: actionTypes.STORE_GENRES,
            genres: genresArray
        })
    }
}

export default connect(dataFromStore, dispatching)(Home);
