import React, { Component } from 'react';

import classes from './Login.module.css';

import Input from '../../components/UI/Input/Input';
import Button from '../../components/UI/Button/Button';

class Login extends Component {

    state = {
        formElements: {
            username: {
                type: 'text',
                placeholder: 'Username',
                value: ''
            },
            password: {
                type: 'password',
                placeholder: 'Password',
                value: ''
            }
        }
    }

    inputChangeHandler = (event, element) => {
        let elementCopy = {...this.state.formElements[element]};
        elementCopy.value = event.target.value;
        console.log(elementCopy);
        this.setState({
            formElements: {
                element: elementCopy
            }
        });
    }

    login = () => {

    }

    render () {
        return (
            <div className={classes.Login}>
                <form onSubmit={this.login}>
                    <h1>Login</h1>
                    {Object.keys(this.state.formElements).map(formElement => {
                        return <Input 
                                    key={formElement}
                                    type={this.state.formElements[formElement].type} 
                                    placeholder={this.state.formElements[formElement].placeholder} 
                                    value={this.state.formElements[formElement].value}
                                    action={(event) => this.inputChangeHandler(event, formElement)}/>;
                    })}
                    <Button>Login</Button>
                </form>
            </div>
        );
    }

}

export default Login;
