import React, { useState } from 'react';
import { connect } from 'react-redux';
import axios from 'axios';

import classes from './GenreSlider.module.css';

const GenreSlider = (props) => {

    const [movies, setMovies] = useState({});

    for (let genre of props.genres) {
        movies[genre.name] = loadMoviesHandler(genre.id);
        console.log(movies[genre.name]);
    }

    async function loadMoviesHandler (id) {
        await axios.get(`https://api.themoviedb.org/3/discover/movie?with_genres=${id}&api_key=d38aa8716411ef7d8e9054b34a6678ac`)
            .then(response => movies = response);
    }


    return (
        <div className={classes.GenreSlider}>
            <p>{props.genre}</p>
        </div>
    );
};

const dataFromStore = state => {
    return {
        genres: state.genres
    }
}

export default connect(dataFromStore)(GenreSlider);
