import React, { Component } from 'react';
import { connect } from 'react-redux';
import axios from 'axios';

import GenreSlider from '../../components/GenreSlider/GenreSlider';

import classes from './Home.module.css';

class Home extends Component {

    state = {
        genres: null
    }

    componentDidMount() {
        this.loadGenresHandler();
    }

    async loadGenresHandler() {
        const result = await axios.get(`https://api.themoviedb.org/3/genre/movie/list?api_key=${this.props.apiKey}&language=en-US`);
        this.setState({
            genres: result.data.genres
        });
    }

    render () {
        return (
            <div className={classes.Home}>
                {this.state.genres.map(genre => {
                    return <GenreSlider key={genre.id} genre={genre.name}/>;
                })}
            </div>
        );

    }
}

const dataFromStore = state => {
    return {
        apiKey: state.apiKey,
        genres: state.genres
    }
}

export default connect(dataFromStore)(Home);
