import React from 'react';
import { connect } from 'react-redux';
import axios from 'axios';

import classes from './GenreSlider.module.css';

const GenreSlider = (props) => {

    async function loadMoviesHandler () {
        let movies = await axios.get(`https://api.themoviedb.org/3/discover/movie?with_genres=${props.id}&api_key=d38aa8716411ef7d8e9054b34a6678ac`)
            .then(response => response);
        return await movies.json();
    }

    async function run () {
        const data = loadMoviesHandler();
        console.log(data);
    }

    run();

    return (
        <div className={classes.GenreSlider}>
            <p>{props.genre}</p>
        </div>
    );
};

const dataFromStore = state => {
    return {
        genres: state.genres
    }
}

export default connect(dataFromStore)(GenreSlider);
