import React, { Component } from 'react';
import { connect } from 'react-redux';

import classes from './MovieThumbnail.module.css';

class MovieThumbnail extends Component {

    componentDidMount () {
        console.log("mounted!");
    }

    shouldComponentUpdate (prevProps, prevState) {
        if (prevProps.row !== this.props.selectedRow) {
            return true;
        } else {
            return false;
        }
    }

    render () {
        let attachedClasses = [classes.MovieThumbnail];
        if (this.props.row === this.props.selectedRow &&
            this.props.column === this.props.selectedColumn) {
            attachedClasses.push(classes.selected);
        }

        return (
            <div className={attachedClasses.join(' ')}>
                <img src={`https://image.tmdb.org/t/p/w500${this.props.thumbnail}`} alt={this.props.title}/>
            </div>
        );
    }
};

const dataFromStore = state => {
    return {
        selectedRow: state.selectedRow,
        selectedColumn: state.selectedColumn
    }
}

export default connect(dataFromStore)(MovieThumbnail);
